  `painter` varchar(50) NOT NULL,
  `publishing_house` varchar(50) NOT NULL,
  `publication_date` varchar(10) NOT NULL,
  `language` varchar(50) NOT NULL,
  `amount` int(2) UNSIGNED ZEROFILL NOT NULL,
  `ISBN` varchar(13) NOT NULL,
  `binding_method` varchar(50) NOT NULL,
  `format` varchar(50) NOT NULL,
  `publish_area` varchar(50) NOT NULL,
  `introduction` varchar(5000) DEFAULT NULL,
  `auther_Introduction` varchar(5000) DEFAULT NULL,
  `list_price` int(3) UNSIGNED NOT NULL,
  `cost_price` int(3) UNSIGNED NOT NULL,
  `discount_price` int(3) UNSIGNED NOT NULL,
  `key_word[array]` varchar(50) NOT NULL,
  `class_one` varchar(50) NOT NULL,
  `class_two` varchar(50) NOT NULL,
  `class_three` varchar(50) NOT NULL,
  `img_cover` varchar(50) NOT NULL,
  `img_1` varchar(50) NOT NULL,
  `img_2` varchar(50) NOT NULL,
  `img_3` varchar(50) NOT NULL,
  `img_4` varchar(50) NOT NULL,
  `img_5` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

